// src/components/EarthCanvas.js
import React, { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls, Stars } from "@react-three/drei";

const Earth = () => {
  const earthRef = useRef();
  
  useFrame((state) => {
    if (earthRef.current) {
      earthRef.current.rotation.y = state.clock.elapsedTime * 0.15;
    }
  });

  return (
    <>
      <mesh ref={earthRef}>
        <sphereGeometry args={[1, 64, 64]} />
        <meshStandardMaterial 
          color="#2E86C1"
          roughness={0.8}
          metalness={0.3}
          emissive="#1B4F72"
          emissiveIntensity={0.2}
        />
      </mesh>
      
      {/* Clouds */}
      <mesh scale={1.02}>
        <sphereGeometry args={[1, 64, 64]} />
        <meshStandardMaterial 
          color="#ffffff"
          transparent
          opacity={0.4}
          roughness={1}
        />
      </mesh>
    </>
  );
};

const EarthCanvas = () => {
  return (
    <Canvas
      camera={{ 
        position: [0, 0, 3],
        fov: 50 
      }}
      style={{
        width: '100%',
        height: '100%'
      }}
    >
      <color attach="background" args={['#0a0a0f']} />
      
      <ambientLight intensity={0.6} />
      <pointLight position={[10, 10, 10]} intensity={1.2} color="#667eea" />
      
      <Earth />
      
      <OrbitControls 
        enableZoom={true}
        enablePan={true}
        enableRotate={true}
        zoomSpeed={0.6}
        panSpeed={0.5}
        rotateSpeed={0.4}
        autoRotate
        autoRotateSpeed={0.3}
        maxPolarAngle={Math.PI}
        minPolarAngle={0}
      />
      
     
    </Canvas>
  );
};

export default EarthCanvas;
