// ==================== Contact Section ==================== 
const ContactSection = ({ form, formLoading, isSubmitted, onChange, onSubmit }) => { 
  const formRef = useRef(); 

  const formVariants = { 
    hidden: { opacity: 0, y: 50 }, 
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        type: "spring", 
        stiffness: 100, 
        damping: 15, 
        duration: 0.6 
      } 
    } 
  }; 

  const inputVariants = { 
    hidden: { opacity: 0, x: 20 }, 
    visible: (i) => ({ 
      opacity: 1, 
      x: 0, 
      transition: { 
        delay: i * 0.1, 
        duration: 0.4 
      } 
    }) 
  }; 

  const contactInfoVariants = { 
    hidden: { opacity: 0, x: -20 }, 
    visible: (i) => ({ 
      opacity: 1, 
      x: 0, 
      transition: { 
        delay: i * 0.1 + 0.3,
        duration: 0.4 
      } 
    }) 
  }; 

  return ( 
    <section id="contact" className="contact-simple"> 
      <div className="container"> 
        <motion.div 
          className="contact-simple-header"
          initial={{ y: -30, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          viewport={{ once: true, amount: 0.3 }}
          transition={{ duration: 0.5 }}
        > 
          <h2 className="contact-simple-subtitle">GET IN TOUCH</h2> 
          <h1 className="contact-simple-title">Contact Me</h1> 
        </motion.div> 

        <div className="contact-container"> 
          <motion.div 
            className="contact-left-column"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true, amount: 0.2 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          > 
            <div className="contact-info-card">
              <div className="contact-info-header">
                <h3>Contact Information</h3>
              </div>

              <div className="contact-info-content">
                <motion.div 
                  className="contact-info-item"
                  custom={0}
                  variants={contactInfoVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                >
                  <div className="contact-info-icon">
                    <span role="img" aria-label="phone">📱</span>
                  </div>
                  <div className="contact-info-details">
                    <h4>Phone</h4>
                    <p>+94 77 123 4567</p>
                  </div>
                </motion.div>

                <motion.div 
                  className="contact-info-item"
                  custom={1}
                  variants={contactInfoVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                >
                  <div className="contact-info-icon">
                    <span role="img" aria-label="email">📧</span>
                  </div>
                  <div className="contact-info-details">
                    <h4>Email</h4>
                    <p>mithila.medhavi@example.com</p>
                  </div>
                </motion.div>

                <motion.div 
                  className="contact-info-item"
                  custom={2}
                  variants={contactInfoVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                >
                  <div className="contact-info-icon">
                    <span role="img" aria-label="address">📍</span>
                  </div>
                  <div className="contact-info-details">
                    <h4>Location</h4>
                    <p>Colombo, Sri Lanka</p>
                  </div>
                </motion.div>

                <motion.div 
                  className="contact-info-item"
                  custom={3}
                  variants={contactInfoVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                >
                  <div className="contact-info-icon">
                    <span role="img" aria-label="social">💼</span>
                  </div>
                  <div className="contact-info-details">
                    <h4>Social Media</h4>
                    <div className="social-links">
                      <a href="#" className="social-link">LinkedIn</a>
                      <a href="#" className="social-link">GitHub</a>
                      <a href="#" className="social-link">Twitter</a>
                    </div>
                  </div>
                </motion.div>
              </div>
            </div>
          </motion.div> 

          <motion.div 
            className="contact-right-column"
            variants={formVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.2 }}
          > 
            <div className="contact-simple-form-card"> 
              <h3 className="form-title">Send Me a Message</h3>
              <p className="form-description">
                Have a project in mind or want to collaborate? Feel free to reach out!
              </p>

              <AnimatePresence> 
                {isSubmitted && ( 
                  <motion.div 
                    className="success-message-simple"
                    initial={{ opacity: 0, y: -20, scale: 0.9 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    exit={{ opacity: 0, y: -20, scale: 0.9 }}
                    transition={{ type: "spring", stiffness: 200 }}
                  > 
                    <motion.span 
                      className="success-icon-simple"
                      animate={{ scale: [1, 1.2, 1] }}
                      transition={{ duration: 0.5 }}
                    > 
                      ✅ 
                    </motion.span> 
                    <span>Thank you! Your message has been sent successfully.</span> 
                  </motion.div> 
                )} 
              </AnimatePresence> 

              <form 
                ref={formRef}
                onSubmit={onSubmit}
                className="contact-simple-form"
              > 
                <motion.div 
                  className="form-group-simple"
                  custom={0}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                > 
                  <label htmlFor="name" className="form-label-simple"> 
                    Your Name 
                  </label> 
                  <input 
                    type="text"
                    id="name"
                    name="name"
                    value={form.name}
                    onChange={onChange}
                    className="form-input-simple"
                    placeholder="What's your name?"
                    required 
                  /> 
                </motion.div> 

                <motion.div 
                  className="form-group-simple"
                  custom={1}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                > 
                  <label htmlFor="email" className="form-label-simple"> 
                    Your Email 
                  </label> 
                  <input 
                    type="email"
                    id="email"
                    name="email"
                    value={form.email}
                    onChange={onChange}
                    className="form-input-simple"
                    placeholder="What's your email?"
                    required 
                  /> 
                </motion.div> 

                <motion.div 
                  className="form-group-simple"
                  custom={2}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                > 
                  <label htmlFor="message" className="form-label-simple"> 
                    Your Message 
                  </label> 
                  <textarea 
                    id="message"
                    name="message"
                    value={form.message}
                    onChange={onChange}
                    className="form-textarea-simple"
                    rows="5"
                    placeholder="What do you want to say?"
                    required 
                  /> 
                </motion.div> 

                <motion.div 
                  custom={3}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                > 
                  <motion.button 
                    type="submit"
                    className="submit-btn-simple"
                    disabled={formLoading}
                    whileHover={{ scale: 1.03 }}
                    whileTap={{ scale: 0.98 }}
                    animate={{
                      background: formLoading 
                        ? ['#667eea', '#764ba2', '#667eea'] 
                        : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
                    }}
                    transition={{
                      background: {
                        duration: 2,
                        repeat: formLoading ? Infinity : 0,
                        ease: "linear"
                      }
                    }}
                  > 
                    {formLoading ? ( 
                      <> 
                        <motion.span 
                          className="loading-spinner-simple"
                          animate={{ rotate: 360 }}
                          transition={{
                            duration: 1,
                            repeat: Infinity,
                            ease: "linear"
                          }}
                        ></motion.span> 
                        Sending... 
                      </> 
                    ) : ( 
                      'Send Message'
                    )} 
                  </motion.button> 
                </motion.div> 
              </form> 
            </div> 
          </motion.div> 
        </div> 
      </div> 
    </section> 
  ); 
};

