import React, { useState, useEffect, useRef } from 'react'; 
import './App.css'; 
import jsPDF from 'jspdf'; 
import { Canvas } from "@react-three/fiber"; 
import { motion, AnimatePresence } from 'framer-motion'; 
import { OrbitControls, Preload, useGLTF } from "@react-three/drei"; 
import CanvasLoader from "./components/Loader"; 
import { FaReact, FaJs, FaHtml5, FaCss3Alt, FaGitAlt, FaFigma, FaChrome, FaNodeJs, FaNpm, FaGithub, FaPython, FaDocker, FaToolbox, FaDatabase, FaCloud } from "react-icons/fa"; 
import { SiTypescript, SiSass, SiWebpack, SiVisualstudiocode, SiPostman, SiMongodb, SiFirebase, SiTailwindcss, SiRedux, SiJest, SiMysql, SiExpress } from "react-icons/si"; 
import EarthCanvas from './components/EarthCanvas'; 

// ==================== KOTA 1: Loading Screen Component ==================== 
const LoadingScreen = ({ isLoading }) => { 
  if (!isLoading) return null; 

  return ( 
    <div className="loading-screen"> 
      <div className="loading-container"> 
        <div className="neon-spinner"></div> 
        <h2 className="loading-text">Initializing Portfolio</h2> 
        <div className="loading-progress"> 
          <div className="progress-bar"></div> 
        </div> 
      </div> 
    </div> 
  ); 
}; 

// ==================== KOTA 2: Navigation Component ==================== 
const Navigation = ({ activeSection, isMenuOpen, isDarkMode, onNavClick, onMenuToggle, onThemeToggle, onDownloadCV }) => { 
  return ( 
    <nav className={`navbar ${isMenuOpen ? 'nav-open' : ''}`}> 
      <div className="nav-container"> 
        <div className="logo"> 
          <span className="logo-text">Mithila</span> 
          <span className="logo-dot"></span> 
        </div> 
        <div className="nav-toggle" onClick={onMenuToggle}> 
          <span className={`toggle-line ${isMenuOpen ? 'active' : ''}`}></span> 
          <span className={`toggle-line ${isMenuOpen ? 'active' : ''}`}></span> 
          <span className={`toggle-line ${isMenuOpen ? 'active' : ''}`}></span> 
        </div> 
        <ul className="nav-menu"> 
          {['home', 'about', 'skills', 'projects', 'contact'].map((section) => ( 
            <li key={section}> 
              <a 
                href={`#${section}`}
                className={`nav-link ${activeSection === section ? 'active' : ''}`}
                onClick={() => onNavClick(section)}
              > 
                <span className="link-text">{section.charAt(0).toUpperCase() + section.slice(1)}</span> 
                <span className="link-underline"></span> 
              </a> 
            </li> 
          ))} 

        </ul> 
      </div> 
    </nav> 
  ); 
}; 

// ==================== KOTA 3: Hero Section ==================== 
const HeroSection = ({ onScrollToSection, onDownloadCV }) => { 
  const scrollToSection = (sectionId) => { 
    const element = document.getElementById(sectionId); 
    if (element) { 
      element.scrollIntoView({ behavior: 'smooth' }); 
    } 
  }; 

  return ( 
    <section id="home" className="hero"> 
      <div className="hero-background"> 
        <div className="hero-particles"></div> 
      </div> 
      <div className="hero-content"> 
        <div className="hero-text"> 
          <div className="greeting">Hello, I'm</div> 
          <h1 className="hero-title"> 
            <span className="title-main">Mithila</span> 
            <span className="title-sub">Medhavi</span> 
          </h1> 
          <div className="hero-subtitle"> 
            <span className="subtitle-text">UI/UX designer</span> 
            <div className="typing-cursor"></div> 
          </div> 
          <p className="hero-description"> 
            I craft <span className="highlight-word">digital experiences</span> that blend beautiful design with seamless functionality. Specializing in <span className="tech-stack"> React, JavaScript,</span> and modern web technologies. 
          </p> 
          <div className="hero-buttons"> 
            <button className="btn btn-download" onClick={onDownloadCV}> 
              <span className="btn-icon"></span> 
              Download CV 
            </button> 
          </div> 
        </div> 
        <div className="hero-visual"> 
          <div className="profile-container"> 
            <div className="profile-card"> 
              <div className="profile-image"> 
                <div className="image-glow"></div> 
              </div> 
            </div> 
          </div> 
        </div> 
      </div> 
      <div className="scroll-indicator"> 
        <div className="scroll-text">Scroll to Explore</div> 
        <div className="scroll-arrow"> 
          <div className="arrow-line"></div> 
        </div> 
      </div> 
    </section> 
  ); 
}; 

// ==================== KOTA 4: About Section ==================== 
const AboutSection = ({ onDownloadCV }) => { 
  return ( 
    <section id="about" className="about"> 
      <div className="container"> 
        <div className="section-header"> 
          <h2 className="section-title"> 
            <span className="title-number">01</span> 
            About Me 
          </h2> 
          <div className="section-subtitle">Get to know me better</div> 
        </div> 
        <div className="about-content"> 
          <div className="about-text"> 
            <div className="about-intro"> 
              <h3>Passionate Frontend Developer from Sri Lanka</h3> 
              <p> 
                With over <span className="highlight">3 years</span> of experience in web development, I specialize in creating responsive, user-friendly applications that provide exceptional user experiences. 
              </p> 
            </div> 
            <div className="about-details"> 
              <div className="detail-item"> 
                <h4> My Focus</h4> 
                <p>React.js, Modern JavaScript, CSS Architecture</p> 
              </div> 
              <div className="detail-item"> 
                <h4> My Approach</h4> 
                <p>Clean code, performance optimization, user-centric design</p> 
              </div> 
              <div className="detail-item"> 
                <h4> My Goal</h4> 
                <p>Build digital products that make a difference</p> 
              </div> 
            </div> 
            <div className="skills-preview"> 
              <h4>Core Technologies</h4> 
              <div className="skills-tags"> 
                {['React.js', 'JavaScript', 'TypeScript', 'CSS3', 'HTML5', 'Node.js', 'Git', 'Webpack'].map(skill => ( 
                  <span key={skill} className="skill-tag">{skill}</span> 
                ))} 
              </div> 
            </div> 
          </div> 
          <div className="about-visual"> 
            <div className="experience-card"> 
              <div className="card-header"> 
                <h3>Experience & Education</h3> 
              </div> 
              <div className="card-content"> 
                <div className="timeline"> 
                  <div className="timeline-item"> 
                    <div className="timeline-year">2021 - Present</div> 
                    <div className="timeline-content"> 
                      <h4>Frontend Developer</h4> 
                      <p>Freelance & Contract Projects</p> 
                    </div> 
                  </div> 
                  <div className="timeline-item"> 
                    <div className="timeline-year">2020 - 2021</div> 
                    <div className="timeline-content"> 
                      <h4>Web Development Intern</h4> 
                      <p>Tech Startup Colombo</p> 
                    </div> 
                  </div> 
                  <div className="timeline-item"> 
                    <div className="timeline-year">2016 - 2020</div> 
                    <div className="timeline-content"> 
                      <h4>BSc Computer Science</h4> 
                      <p>University of Colombo</p> 
                    </div> 
                  </div> 
                </div> 
              </div> 
            </div> 
          </div> 
        </div> 
      </div> 
    </section> 
  ); 
}; 

const SkillsSection = () => { 
  const skills = [ 
    { id: 1, name: "HTML", image: "/icons/html.png", color: "#e34f26" }, 
    { id: 2, name: "CSS", image: "/icons/css.png", color: "#1572b6" }, 
    { id: 3, name: "JavaScript", image: "/icons/js.png", color: "#f7df1e" }, 
    { id: 4, name: "React", image: "/icons/react.png", color: "#61dafb" }, 
    { id: 5, name: "Node", image: "/icons/node.png", color: "#339933" }, 
    { id: 6, name: "Python", image: "/icons/python.png", color: "#3776ab" }, 
  ]; 

  return ( 
    <section className="skills-3d-section"> 
      <div className="section-header"> 
        <h2>My Tech Stack</h2> 
        <p className="section-subtitle">Skills in One Row</p> 
      </div> 
      <div className="skills-3d-container"> 
        <div className="skills-row"> 
          {skills.map((skill) => ( 
            <div 
              key={skill.id}
              className="hexagon-3d-item"
              style={{ "--skill-color": skill.color }}
            > 
              <div className="hexagon-3d"> 
                {/* FRONT */} 
                <div className="hexagon-3d-front"> 
                  <div className="skill-circle"> 
                    <img src={skill.image} alt={skill.name} className="skill-icon-image" /> 
                  </div> 
                </div> 
                {/* BACK  EMPTY (no percentage) */} 
                <div className="hexagon-3d-back"></div> 
              </div> 
            </div> 
          ))} 
        </div> 
      </div> 
    </section> 
  ); 
}; 

// ==================== KOTA 6: Projects Section ==================== 
const ProjectsSection = () => { 
  const projects = [ 
    { 
      id: 3, 
      title: "E-Commerce Platform", 
      description: "Full-stack e-commerce solution with React, Node.js, and MongoDB", 
      tech: ['React', 'Node.js', 'MongoDB', 'Stripe'], 
      githubUrl: "https://github.com/yourusername/ecommerce-platform", 
      liveDemoUrl: "https://ecommerce-demo.vercel.app", 
      featured: false 
    }, 
    { 
      id: 4, 
      title: "Task Management App", 
      description: "Productivity application with real-time collaboration", 
      tech: ['React', 'Firebase', 'Material UI'], 
      githubUrl: "https://github.com/yourusername/task-manager", 
      liveDemoUrl: "https://taskmanager-demo.vercel.app", 
      featured: false 
    }, 
    { 
      id: 5, 
      title: "Portfolio Website", 
      description: "Modern portfolio with advanced animations and interactions", 
      tech: ['React', 'GSAP', 'CSS3'], 
      githubUrl: "https://github.com/yourusername/portfolio", 
      liveDemoUrl: "https://portfolio-demo.vercel.app", 
      featured: false 
    } 
  ]; 

  return ( 
    <section id="projects" className="projects"> 
      <div className="container"> 
        <div className="section-header"> 
          <h2 className="section-title"> 
            <span className="title-number">03</span> 
            My Projects 
          </h2> 
          <div className="section-subtitle">Some of my recent work</div> 
        </div> 
        <div className="projects-showcase"> 
          <div className="project-grid"> 
            {projects.map(project => ( 
              <div key={project.id} className={`project-card ${project.featured ? 'featured' : ''}`}> 
                <div className="project-image"> 
                  <div className="image-overlay"> 
                    <div className="project-links"> 
                      <a 
                        href={project.liveDemoUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="link-btn"
                      > 
                        Live Demo 
                      </a> 
                      <a 
                        href={project.githubUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="link-btn secondary"
                      > 
                        Source Code 
                      </a> 
                    </div> 
                  </div> 
                  {/* Project image placeholder - You can add actual images */} 
                  <div className="image-placeholder"> 
                    <span>{project.title}</span> 
                  </div> 
                </div> 
                <div className="project-info"> 
                  {project.featured && ( 
                    <span className="project-badge">Featured Project</span> 
                  )} 
                  <h3>{project.title}</h3> 
                  <p>{project.description}</p> 
                  <div className="project-tech"> 
                    {project.tech.map((tech, index) => ( 
                      <span key={index} className="tech-tag">{tech}</span> 
                    ))} 
                  </div> 
                </div> 
              </div> 
            ))} 
          </div> 
        </div> 
      </div> 
    </section> 
  ); 
}; 

// ==================== Contact Section (Animated Version) ==================== 
const ContactSection = ({ form, formLoading, isSubmitted, onChange, onSubmit }) => { 
  const formRef = useRef(); 

  const formVariants = { 
    hidden: { opacity: 0, y: 50 }, 
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        type: "spring", 
        stiffness: 100, 
        damping: 15, 
        duration: 0.6 
      } 
    } 
  }; 

  const inputVariants = { 
    hidden: { opacity: 0, x: -20 }, 
    visible: (i) => ({ 
      opacity: 1, 
      x: 0, 
      transition: { 
        delay: i * 0.1, 
        duration: 0.4 
      } 
    }) 
  }; 

  return ( 
    <section id="contact" className="contact-simple"> 
      <div className="container"> 
        {/* Animated Header */} 
        <motion.div 
          className="contact-simple-header"
          initial={{ y: -30, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          viewport={{ once: true, amount: 0.3 }}
          transition={{ duration: 0.5 }}
        > 
          <h2 className="contact-simple-subtitle">GET IN TOUCH</h2> 
          <h1 className="contact-simple-title">Contact.</h1> 
        </motion.div> 

        {/* Contact Container - Two Columns */} 
        <div className="contact-container"> 
          {/* Left Column - Form */} 
          <motion.div 
            className="contact-left-column"
            variants={formVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.2 }}
          > 
            <div className="contact-simple-form-card"> 
              <AnimatePresence> 
                {isSubmitted && ( 
                  <motion.div 
                    className="success-message-simple"
                    initial={{ opacity: 0, y: -20, scale: 0.9 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    exit={{ opacity: 0, y: -20, scale: 0.9 }}
                    transition={{ type: "spring", stiffness: 200 }}
                  > 
                    <motion.span 
                      className="success-icon-simple"
                      animate={{ scale: [1, 1.2, 1] }}
                      transition={{ duration: 0.5 }}
                    > 

                    </motion.span> 
                    <span>Thank you! Your message has been sent successfully.</span> 
                  </motion.div> 
                )} 
              </AnimatePresence> 

              <form 
                ref={formRef}
                onSubmit={onSubmit}
                className="contact-simple-form"
              > 
                {/* Name Field */} 
                <motion.div 
                  className="form-group-simple"
                  custom={0}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                > 
                  <label htmlFor="name" className="form-label-simple"> 
                    Your Name 
                  </label> 
                  <input 
                    type="text"
                    id="name"
                    name="name"
                    value={form.name}
                    onChange={onChange}
                    className="form-input-simple"
                    placeholder="What's your name?"
                    required 
                  /> 
                </motion.div> 

                {/* Email Field */} 
                <motion.div 
                  className="form-group-simple"
                  custom={1}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                > 
                  <label htmlFor="email" className="form-label-simple"> 
                    Your Email 
                  </label> 
                  <input 
                    type="email"
                    id="email"
                    name="email"
                    value={form.email}
                    onChange={onChange}
                    className="form-input-simple"
                    placeholder="What's your email?"
                    required 
                  /> 
                </motion.div> 

                {/* Message Field */} 
                <motion.div 
                  className="form-group-simple"
                  custom={2}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                > 
                  <label htmlFor="message" className="form-label-simple"> 
                    Your Message 
                  </label> 
                  <textarea 
                    id="message"
                    name="message"
                    value={form.message}
                    onChange={onChange}
                    className="form-textarea-simple"
                    rows="5"
                    placeholder="What do you want to say?"
                    required 
                  /> 
                </motion.div> 

                {/* Submit Button */} 
                <motion.div 
                  custom={3}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                > 
                  <motion.button 
                    type="submit"
                    className="submit-btn-simple"
                    disabled={formLoading}
                    whileHover={{ scale: 1.03 }}
                    whileTap={{ scale: 0.98 }}
                    animate={{
                      background: formLoading 
                        ? ['#667eea', '#764ba2', '#667eea'] 
                        : 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
                    }}
                    transition={{
                      background: {
                        duration: 2,
                        repeat: formLoading ? Infinity : 0,
                        ease: "linear"
                      }
                    }}
                  > 
                    {formLoading ? ( 
                      <> 
                        <motion.span 
                          className="loading-spinner-simple"
                          animate={{ rotate: 360 }}
                          transition={{
                            duration: 1,
                            repeat: Infinity,
                            ease: "linear"
                          }}
                        ></motion.span> 
                        Sending... 
                      </> 
                    ) : ( 
                      'Send'
                    )} 
                  </motion.button> 
                </motion.div> 

                {/* Contact Info (inside form but styled differently) */} 
                <div className="contact-info-section"> 
                  <div className="info-item"> 
                    <div className="info-icon-wrapper"> 
                      <span role="img" aria-label="phone"></span> 
                    </div> 
                    <div className="info-content"> 
                      <span className="info-label">Phone</span> 
                      <span className="info-value">+94 77 123 4567</span> 
                    </div> 
                  </div> 
                  <div className="info-item"> 
                    <div className="info-icon-wrapper"> 
                      <span role="img" aria-label="email"></span> 
                    </div> 
                    <div className="info-content"> 
                      <span className="info-label">Email</span> 
                      <span className="info-value">mithila.medhavi@example.com</span> 
                    </div> 
                  </div> 
                  <div className="info-item"> 
                    <div className="info-icon-wrapper"> 
                      <span role="img" aria-label="address"></span> 
                    </div> 
                    <div className="info-content"> 
                      <span className="info-label">Address</span> 
                      <span className="info-value">Colombo, Sri Lanka</span> 
                    </div> 
                  </div> 
                </div> 
              </form> 
            </div> 
          </motion.div> 

          {/* Right Column - Earth Canvas */} 
          <motion.div 
            className="contact-right-column"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true, amount: 0.2 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          > 
            <div className="earth-canvas-container"> 
              <EarthCanvas /> 
            </div> 
          </motion.div> 
        </div> 
      </div> 
    </section> 
  ); 
}; 

