import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import jsPDF from 'jspdf';
import { motion, AnimatePresence } from 'framer-motion';
import EarthCanvas from './components/EarthCanvas';

// ==================== Loading Screen ====================
const LoadingScreen = ({ isLoading }) => {
  if (!isLoading) return null;

  return (
    <div className="loading-screen dark-mode">
      <div className="loading-container">
        <div className="neon-spinner"></div>
        <h2 className="loading-text">Initializing Portfolio</h2>
        <div className="loading-progress">
          <div className="progress-bar"></div>
        </div>
      </div>
    </div>
  );
};

// ==================== Navigation ====================
const Navigation = ({ activeSection, isMenuOpen, onNavClick, onMenuToggle, onDownloadCV }) => {
  return (
    <nav className={`navbar dark-mode ${isMenuOpen ? 'nav-open' : ''}`}>
      <div className="nav-container">
        <div className="logo">
          <span className="logo-text">Mithila</span>
          <span className="logo-dot"></span>
        </div>

        <div className="nav-toggle" onClick={onMenuToggle}>
          <span className="toggle-line active"></span>
          <span className="toggle-line active"></span>
          <span className="toggle-line active"></span>
        </div>

        <ul className="nav-menu">
          {['home', 'about', 'skills', 'projects', 'contact'].map(section => (
            <li key={section}>
              <a
                href={`#${section}`}
                className={`nav-link ${activeSection === section ? 'active' : ''} dark-mode`}
                onClick={() => onNavClick(section)}
              >
                {section.toUpperCase()}
              </a>
            </li>
          ))}
          <li>
            <button className="nav-download-btn" onClick={onDownloadCV}>
              ðŸ“¥ Download CV
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
};

// ==================== Hero ====================
const HeroSection = ({ onDownloadCV }) => (
  <section id="home" className="hero dark-mode">
    <div className="hero-content">
      <div className="hero-text">
        <h1>Mithila Medhavi</h1>
        <h3>Frontend Developer</h3>
        <p>
          I build modern, performant and visually stunning web applications using
          React and modern JavaScript technologies.
        </p>
        <button className="btn btn-download" onClick={onDownloadCV}>
          ðŸ“¥ Download CV
        </button>
      </div>
    </div>
  </section>
);

// ==================== About ====================
const AboutSection = () => (
  <section id="about" className="about dark-mode">
    <div className="container">
      <h2>About Me</h2>
      <p>
        Passionate frontend developer from Sri Lanka with 3+ years of experience
        in building responsive and interactive web applications.
      </p>
    </div>
  </section>
);

// ==================== Skills ====================
const SkillsSection = () => {
  const skills = ['HTML', 'CSS', 'JavaScript', 'React', 'Node.js', 'Python'];

  return (
    <section id="skills" className="skills-3d-section dark-mode">
      <div className="container">
        <h2>Tech Stack</h2>
        <div className="skills-row">
          {skills.map(skill => (
            <div key={skill} className="skill-card dark-mode">
              {skill}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// ==================== Projects ====================
const ProjectsSection = () => (
  <section id="projects" className="projects dark-mode">
    <div className="container">
      <h2>Projects</h2>
      <p>Selected projects showcasing my frontend expertise.</p>
    </div>
  </section>
);

// ==================== Contact ====================
const ContactSection = ({ form, formLoading, isSubmitted, onChange, onSubmit }) => {
  const formRef = useRef();

  return (
    <section id="contact" className="contact-simple dark-mode">
      <div className="container">
        <h2>Contact</h2>

        {isSubmitted && <p className="success-message">Message Sent âœ…</p>}

        <form ref={formRef} onSubmit={onSubmit} className="contact-simple-form">
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            value={form.name}
            onChange={onChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Your Email"
            value={form.email}
            onChange={onChange}
            required
          />
          <textarea
            name="message"
            placeholder="Your Message"
            value={form.message}
            onChange={onChange}
            required
          />
          <button type="submit" disabled={formLoading}>
            {formLoading ? 'Sending...' : 'Send'}
          </button>
        </form>

        <div className="earth-canvas-container">
          <EarthCanvas />
        </div>
      </div>
    </section>
  );
};

// ==================== Footer ====================
const Footer = ({ onDownloadCV }) => (
  <footer className="footer dark-mode">
    <p>Â© {new Date().getFullYear()} Mithila Medhavi</p>
    <button onClick={onDownloadCV}>Download CV</button>
  </footer>
);

// ==================== App ====================
const App = () => {
  const [activeSection, setActiveSection] = useState('home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [formLoading, setFormLoading] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  useEffect(() => {
    document.body.classList.add('dark-mode');
    setTimeout(() => setIsLoading(false), 2000);
  }, []);

  const handleFormChange = e =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleFormSubmit = e => {
    e.preventDefault();
    setFormLoading(true);
    setTimeout(() => {
      setFormLoading(false);
      setIsSubmitted(true);
      setForm({ name: '', email: '', message: '' });
    }, 1500);
  };

  const downloadSimplePDF = () => {
    const pdf = new jsPDF();
    pdf.text('Mithila Medhavi - Frontend Developer', 20, 20);
    pdf.save('Mithila_Medhavi_CV.pdf');
  };

  return (
    <div className="portfolio dark-mode">
      <LoadingScreen isLoading={isLoading} />

      <Navigation
        activeSection={activeSection}
        isMenuOpen={isMenuOpen}
        onNavClick={setActiveSection}
        onMenuToggle={() => setIsMenuOpen(!isMenuOpen)}
        onDownloadCV={downloadSimplePDF}
      />

      <HeroSection onDownloadCV={downloadSimplePDF} />
      <AboutSection />
      <SkillsSection />
      <ProjectsSection />
      <ContactSection
        form={form}
        formLoading={formLoading}
        isSubmitted={isSubmitted}
        onChange={handleFormChange}
        onSubmit={handleFormSubmit}
      />
      <Footer onDownloadCV={downloadSimplePDF} />
    </div>
  );
};

export default App;
