import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import jsPDF from 'jspdf';
import { Canvas } from "@react-three/fiber";
import { motion, AnimatePresence } from 'framer-motion';
import { OrbitControls, Preload, useGLTF } from "@react-three/drei";

import CanvasLoader from "./components/Loader";

import { 
  FaReact, FaJs, FaHtml5, FaCss3Alt, FaGitAlt, FaFigma,
  FaChrome, FaNodeJs, FaNpm, FaGithub, FaPython, FaDocker,
  FaToolbox, FaDatabase, FaCloud 
} from "react-icons/fa";
import { 
  SiTypescript, SiSass, SiWebpack, SiVisualstudiocode,
  SiPostman, SiMongodb, SiFirebase, SiTailwindcss,
  SiRedux, SiJest, SiMysql, SiExpress 
} from "react-icons/si";
import EarthCanvas from './components/EarthCanvas';

// ==================== KOTA 1: Loading Screen Component ====================
const LoadingScreen = ({ isLoading }) => {
  if (!isLoading) return null;

  return (
    <div className="loading-screen">
      <div className="loading-container">
        <div className="neon-spinner"></div>
        <h2 className="loading-text">Initializing Portfolio</h2>
        <div className="loading-progress">
          <div className="progress-bar"></div>
        </div>
      </div>
    </div>
  );
};

// ==================== KOTA 2: Navigation Component ====================
const Navigation = ({ 
  activeSection, 
  isMenuOpen, 
  isDarkMode, 
  onNavClick, 
  onMenuToggle, 
  onThemeToggle,
  onDownloadCV 
}) => {
  return (
    <nav className={`navbar ${isMenuOpen ? 'nav-open' : ''}`}>
      <div className="nav-container">
        <div className="logo">
          <span className="logo-text">Mithila</span>
          <span className="logo-dot"></span>
        </div>

        <div className="nav-toggle" onClick={onMenuToggle}>
          <span className={`toggle-line ${isMenuOpen ? 'active' : ''}`}></span>
          <span className={`toggle-line ${isMenuOpen ? 'active' : ''}`}></span>
          <span className={`toggle-line ${isMenuOpen ? 'active' : ''}`}></span>
        </div>

        <ul className="nav-menu">
          {['home', 'about', 'skills', 'projects', 'contact'].map((section) => (
            <li key={section}>
              <a 
                href={`#${section}`}
                className={`nav-link ${activeSection === section ? 'active' : ''}`}
                onClick={() => onNavClick(section)}
              >
                <span className="link-text">{section.charAt(0).toUpperCase() + section.slice(1)}</span>
                <span className="link-underline"></span>
              </a>
            </li>
          ))}

          <li className="nav-buttons">


            <button className="theme-toggle-btn" onClick={onThemeToggle}>
              <div className="theme-toggle-content">
                <div className="theme-icon">
                  {isDarkMode ? (
                    <div className="sun-icon">
                      <div className="sun-rays">
                        {[...Array(8)].map((_, i) => (
                          <span key={i}></span>
                        ))}
                      </div>
                      <div className="sun-core"></div>
                    </div>
                  ) : (
                    <div className="moon-icon">
                      <div className="moon-crater"></div>
                      <div className="moon-crater"></div>
                      <div className="moon-crater"></div>
                      <div className="moon-glow"></div>
                    </div>
                  )}
                </div>
                <span className="theme-text">
                  {isDarkMode ? 'DAY MODE' : 'NIGHT MODE'}
                </span>
              </div>
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
};

// ==================== KOTA 3: Hero Section ====================
const HeroSection = ({ onScrollToSection, onDownloadCV }) => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="hero">
      <div className="hero-background">
        <div className="hero-particles"></div>
      </div>

      <div className="hero-content">
        <div className="hero-text">
          <div className="greeting">Hello, I'm</div>
          <h1 className="hero-title">
            <span className="title-main">Mithila</span>
            <span className="title-sub">Medhavi</span>
          </h1>
          <div className="hero-subtitle">
            <span className="subtitle-text">UI/UX designer</span>
            <div className="typing-cursor"></div>
          </div>
          <p className="hero-description">
            I craft <span className="highlight-word">digital experiences</span> that blend 
            beautiful design with seamless functionality. Specializing in 
            <span className="tech-stack"> React, JavaScript,</span> and modern web technologies.
          </p>



          <div className="hero-buttons">

            <button className="btn btn-download" onClick={onDownloadCV}>
              <span className="btn-icon"></span>
              Download CV
            </button>
          </div>
        </div>

        <div className="hero-visual">
          <div className="profile-container">
            <div className="profile-card">
              <div className="profile-image">
                <div className="image-glow"></div>

              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="scroll-indicator">
        <div className="scroll-text">Scroll to Explore</div>
        <div className="scroll-arrow">
          <div className="arrow-line"></div>
        </div>
      </div>
    </section>
  );
};

// ==================== KOTA 4: About Section ====================
const AboutSection = ({ onDownloadCV }) => {
  return (
    <section id="about" className="about">
      <div className="container">
        <div className="section-header">
          <h2 className="section-title">
            <span className="title-number">01</span>
            About Me
          </h2>
          <div className="section-subtitle">Get to know me better</div>
        </div>

        <div className="about-content">
          <div className="about-text">
            <div className="about-intro">
              <h3>Passionate Frontend Developer from Sri Lanka</h3>
              <p>
                With over <span className="highlight">3 years</span> of experience in web development, 
                I specialize in creating responsive, user-friendly applications that 
                provide exceptional user experiences.
              </p>
            </div>

            <div className="about-details">
              <div className="detail-item">
                <h4> My Focus</h4>
                <p>React.js, Modern JavaScript, CSS Architecture</p>
              </div>
              <div className="detail-item">
                <h4> My Approach</h4>
                <p>Clean code, performance optimization, user-centric design</p>
              </div>
              <div className="detail-item">
                <h4> My Goal</h4>
                <p>Build digital products that make a difference</p>
              </div>
            </div>

            <div className="skills-preview">
              <h4>Core Technologies</h4>
              <div className="skills-tags">
                {['React.js', 'JavaScript', 'TypeScript', 'CSS3', 'HTML5', 'Node.js', 'Git', 'Webpack'].map(skill => (
                  <span key={skill} className="skill-tag">{skill}</span>
                ))}
              </div>
            </div>


          </div>

          <div className="about-visual">
            <div className="experience-card">
              <div className="card-header">
                <h3>Experience & Education</h3>
              </div>
              <div className="card-content">
                <div className="timeline">
                  <div className="timeline-item">
                    <div className="timeline-year">2021 - Present</div>
                    <div className="timeline-content">
                      <h4>Frontend Developer</h4>
                      <p>Freelance & Contract Projects</p>
                    </div>
                  </div>
                  <div className="timeline-item">
                    <div className="timeline-year">2020 - 2021</div>
                    <div className="timeline-content">
                      <h4>Web Development Intern</h4>
                      <p>Tech Startup Colombo</p>
                    </div>
                  </div>
                  <div className="timeline-item">
                    <div className="timeline-year">2016 - 2020</div>
                    <div className="timeline-content">
                      <h4>BSc Computer Science</h4>
                      <p>University of Colombo</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};










const SkillsSection = () => {
  const skills = [
    { id: 1, name: "HTML", image: "/icons/html.png", color: "#e34f26" },
    { id: 2, name: "CSS", image: "/icons/css.png", color: "#1572b6" },
    { id: 3, name: "JavaScript", image: "/icons/js.png", color: "#f7df1e" },
    { id: 4, name: "React", image: "/icons/react.png", color: "#61dafb" },
    { id: 5, name: "Node", image: "/icons/node.png", color: "#339933" },
    { id: 6, name: "Python", image: "/icons/python.png", color: "#3776ab" },
  ];

  return (
    <section className="skills-3d-section">
      <div className="section-header">
        <h2>My Tech Stack</h2>
        <p className="section-subtitle">Skills in One Row</p>
      </div>

      <div className="skills-3d-container">
        <div className="skills-row">
          {skills.map((skill) => (
            <div
              key={skill.id}
              className="hexagon-3d-item"
              style={{ "--skill-color": skill.color }}
            >
              <div className="hexagon-3d">
                {/* FRONT */}
                <div className="hexagon-3d-front">
                  <div className="skill-circle">
                    <img
                      src={skill.image}
                      alt={skill.name}
                      className="skill-icon-image"
                    />
                  </div>
                </div>

                {/* BACK  EMPTY (no percentage) */}
                <div className="hexagon-3d-back"></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};















// ==================== KOTA 6: Projects Section ====================
