                    name="name"
                    value={form.name}
                    onChange={onChange}
                    className="form-input-simple"
                    placeholder="What's your name?"
                    required
                  />
                </motion.div>

                {/* Email Field */}
                <motion.div 
                  className="form-group-simple"
                  custom={1}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                >
                  <label htmlFor="email" className="form-label-simple">
                    Your Email
                  </label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={form.email}
                    onChange={onChange}
                    className="form-input-simple"
                    placeholder="What's your email?"
                    required
                  />
                </motion.div>

                {/* Message Field */}
                <motion.div 
                  className="form-group-simple"
                  custom={2}
                  variants={inputVariants}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                >
                  <label htmlFor="message" className="form-label-simple">
