// ==================== MAIN APP COMPONENT ====================
const App = () => {
  // State Declarations
  const [activeSection, setActiveSection] = useState('home');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [scrollProgress, setScrollProgress] = useState(0);

  // Contact form state
  const [form, setForm] = useState({
    name: "",
    email: "",
    message: "",
  });
  const [formLoading, setFormLoading] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const portfolioRef = useRef();

  // Effects
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 3000);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [isDarkMode]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    const handleScroll = () => {
      const scrollTop = window.pageYOffset;
      const docHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = (scrollTop / docHeight) * 100;
      setScrollProgress(progress);
    };

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Event Handlers
  const handleNavClick = (section) => {
    setActiveSection(section);
    setIsMenuOpen(false);
  };

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value,
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    setFormLoading(true);

    // Simulate email sending
    setTimeout(() => {
      setFormLoading(false);
      setIsSubmitted(true);

      console.log('Contact form submitted:', form);

      // Reset form
      setForm({
        name: "",
        email: "",
        message: "",
      });

      // Hide success message after 5 seconds
      setTimeout(() => {
        setIsSubmitted(false);
      }, 5000);
    }, 2000);
  };

  const downloadSimplePDF = () => {
    const pdf = new jsPDF();

    pdf.setFontSize(20);
    pdf.text('Sandeepa Perera - Portfolio', 105, 15, { align: 'center' });

    pdf.setFontSize(12);
    pdf.text('Frontend Developer | React Specialist', 105, 25, { align: 'center' });
    pdf.text('Email: sandeepa.perera@example.com', 20, 40);
    pdf.text('Phone: +94 77 123 4567', 20, 50);
    pdf.text('Location: Colombo, Sri Lanka', 20, 60);

    pdf.text('Technical Skills:', 20, 80);
    pdf.text('- React.js, JavaScript, TypeScript', 30, 90);
    pdf.text('- HTML5, CSS3, SASS, Tailwind', 30, 100);
    pdf.text('- Node.js, Express, MongoDB', 30, 110);
    pdf.text('- Git, Webpack, REST APIs', 30, 120);

    pdf.text('Professional Experience:', 20, 140);
    pdf.text('- 3+ years in Frontend Development', 30, 150);
    pdf.text('- 25+ Projects Completed', 30, 160);
    pdf.text('- 15+ Happy Clients', 30, 170);

    pdf.text('Education:', 20, 190);
    pdf.text('- BSc in Computer Science', 30, 200);
    pdf.text('- Full Stack Web Development Certification', 30, 210);

    pdf.save('Sandeepa_Perera_CV.pdf');
  };

  // Render Main App
  return (
    <div className={`portfolio ${isDarkMode ? 'dark-mode' : ''}`} ref={portfolioRef}>
      {/* Loading Screen */}
      <LoadingScreen isLoading={isLoading} />

      {/* Background Elements */}
      <BackgroundElements 
        mousePosition={mousePosition} 
        scrollProgress={scrollProgress} 
      />

      {/* Navigation */}
      <Navigation
        activeSection={activeSection}
        isMenuOpen={isMenuOpen}
        isDarkMode={isDarkMode}
        onNavClick={handleNavClick}
        onMenuToggle={() => setIsMenuOpen(!isMenuOpen)}
        onThemeToggle={toggleDarkMode}
        onDownloadCV={downloadSimplePDF}
      />

      {/* Hero Section */}
      <HeroSection 
        onScrollToSection={handleNavClick}
        onDownloadCV={downloadSimplePDF}
      />

      {/* About Section */}
      <AboutSection onDownloadCV={downloadSimplePDF} />

      {/* Skills Section */}
      <SkillsSection />

      {/* Projects Section */}
      <ProjectsSection />

      {/* Contact Section */}
      <ContactSection
        form={form}
        formLoading={formLoading}
        isSubmitted={isSubmitted}
        onChange={handleFormChange}
        onSubmit={handleFormSubmit}
      />

      {/* Footer */}
      <Footer onDownloadCV={downloadSimplePDF} />
    </div>
  );
};

export default App;
